CREATE TABLE "addresses" ("id" uuid NOT NULL, "country_id" uuid NOT NULL, "state_id" uuid NOT NULL, "city_id" uuid, "address_line1" text NOT NULL, "address_line2" text, "zip_code" text NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));