CREATE TABLE "banks" ("id" uuid NOT NULL, "bank_name" text NOT NULL, "branch_name" text NOT NULL, "account_number" text NOT NULL, "ifsc" text NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));