CREATE TABLE "buildings" ("id" integer NOT NULL, "name" text NOT NULL, "building_type_id" integer NOT NULL, "apartment_id" uuid NOT NULL, "total_units" integer NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));