CREATE TABLE "calendars" ("id" uuid NOT NULL, "name" text NOT NULL, "description" text NOT NULL, "is_public" boolean NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, PRIMARY KEY ("id"));