CREATE TABLE "complaint" ("id" uuid NOT NULL, "subject" text NOT NULL, "description" text, "doc_link" text, "preffered_time" timestamp without time zone NOT NULL, "category_id" integer NOT NULL, "sub_category_id" integer NOT NULL, "priority_id" integer NOT NULL, "department_id" integer NOT NULL, "occured_date" timestamp without time zone NOT NULL, "preffered_date" timestamp without time zone NOT NULL, "is_urgent" boolean NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));