CREATE TABLE "delivery_personnel" ("id" integer NOT NULL, "name" text NOT NULL, "company_name" text NOT NULL, "mobile_number" text NOT NULL, "created_by" uuid NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_by" uuid, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, PRIMARY KEY ("id"));