CREATE TABLE "emergency_contacts" ("id" integer NOT NULL, "category_type" text NOT NULL, "name" text NOT NULL, "email" text NOT NULL, "contact_number" text NOT NULL, "photo_graph" bytea NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));