CREATE TABLE "event_occurrences" ("id" integer NOT NULL, "event_id" uuid NOT NULL, "occurrence_date" timestamp without time zone NOT NULL, "start_time" timestamp without time zone NOT NULL, "end_time" timestamp without time zone NOT NULL, "generated_from_recurrence" boolean NOT NULL, "recording_url" text, "notes" text, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "deleted_on_utc" timestamp without time zone, "created_by" uuid NOT NULL, "modified_by" uuid, "EventOccurrenceStatusId" integer DEFAULT 1 NOT NULL, PRIMARY KEY ("id"));