CREATE TABLE "gates" ("id" integer NOT NULL, "name" text NOT NULL, "apartment_id" uuid NOT NULL, "building_id" integer NOT NULL, "gate_type_id" integer NOT NULL, "is_used_for_in" boolean DEFAULT false NOT NULL, "is_used_for_out" boolean DEFAULT false NOT NULL, "is_all_users_allowed" boolean DEFAULT false NOT NULL, "is_residents_allowed" boolean DEFAULT false NOT NULL, "is_staff_allowed" boolean DEFAULT false NOT NULL, "is_vendor_allowed" boolean DEFAULT false NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));