CREATE TABLE "raise_tickets" ("id" uuid NOT NULL, "subject" text NOT NULL, "description" text, "attachment_link" text, "category_id" integer NOT NULL, "sub_category_id" integer NOT NULL, "priority_id" integer NOT NULL, "department_id" integer NOT NULL, "is_urgent" boolean NOT NULL, "occured_date" timestamp without time zone NOT NULL, "preffered_date" timestamp without time zone NOT NULL, "preferred_time" time without time zone NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));