CREATE TABLE "residents" ("id" integer NOT NULL, "first_name" text NOT NULL, "last_name" text, "email" text, "contact_number" text, "permanent_address_id" uuid, "resident_type_id" integer NOT NULL, "occupancy_status_id" integer NOT NULL, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, "apartment_id" uuid DEFAULT '00000000-0000-0000-0000-000000000000'::uuid NOT NULL, "user_id" uuid DEFAULT '00000000-0000-0000-0000-000000000000'::uuid NOT NULL, "is_primary_owner" boolean DEFAULT false NOT NULL, PRIMARY KEY ("id"));