CREATE TABLE "vehicles" ("id" integer NOT NULL, "vehicle_number" text NOT NULL, "vehicle_type_id" integer NOT NULL, "unit_id" integer NOT NULL, "vehicle_rf_id" text, "vehicle_rf_id_secretcode" text, "created_on_utc" timestamp without time zone NOT NULL, "modified_on_utc" timestamp without time zone, "deleted_on_utc" timestamp without time zone, "is_deleted" boolean DEFAULT false NOT NULL, "created_by" uuid NOT NULL, "modified_by" uuid, PRIMARY KEY ("id"));