CREATE TABLE "water_tanker_deliveries" ("id" integer NOT NULL, "company_id" uuid NOT NULL, "vendor_id" uuid NOT NULL, "delivery_date" date NOT NULL, "delivery_time" time without time zone NOT NULL, "tanker_capacity_liters" integer NOT NULL, "actual_received_liters" integer NOT NULL, "created_on_utc" timestamp with time zone NOT NULL, "modified_on_utc" timestamp with time zone, "created_by" uuid NOT NULL, "modified_by" uuid, "is_deleted" boolean DEFAULT false NOT NULL, "deleted_on_utc" timestamp without time zone, PRIMARY KEY ("id"));